INSERT INTO `cvar` (`id`, `id_docente`, `areas`, `experticia`, `grado`, `especializacion`, `maestria`, `doctorado`) VALUES
(1, 1, '[CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS]', 'HISTOLOGÍA: TÉCNICAS CITOHISTOLÓGICAS, HISTOQUÍMICA E INMUNOHISTOQUÍMICA. BIOPESTICIDAS DE ORIGEN VEGETAL.MECANISMOS DE ACCIÓN, VÍAS DE SEÑALIZACIÓN INTRACELULAR.REPRODUCCIÓN', '[BIOQUÍMICA -  Estado Completo]', '[ESPECIALISTA EN DOCENCIA EN ENTORNOS VIRTUALES -  Estado Completo]', '[MAGISTER EN DOCENCIA UNIVERSITARIA EN CIENCIAS DE LA SALUD -  Estado Completo]', '[DOCTOR EN BIOQUÍMICA -  Estado Completo]'),
(2, 2, '[CIENCIAS NATURALES Y EXACTAS - CIENCIAS QUÍMICAS]', 'ELECTROFISIOLOGIA, MANEJO DE ANIMALES DE LABORATORIO,INMUNOHISTOQUIMICA,', '[LICENCIADO EN QUÍMICA FARMACÉUTICA -  Estado Completo]', '[ESPECIALISTA EN TECNOLOGÍAS MULTIMEDIA PARA DESARROLLOS EDUCATIVOS -  Estado Incompleto]', NULL, '[DOCTOR EN CIENCIAS QUÍMICAS -  Estado Completo]'),
(3, 3, '[CIENCIAS AGRÍCOLAS - AGRICULTURA, SILVICULTURA Y PESCA]', 'PLAGAS Y ENFERMEDADES DEL NOGAL', '[INGENIERO AGRÓNOMO -  Estado Completo]', NULL, NULL, NULL),
(4, 4, '[CIENCIAS SOCIALES - CIENCIAS DE LA EDUCACIÓN]', 'MI EXPERTICIA PROFESIONAL SURGE DE MI FORMACIÓN ACADÉMICA. MI TÍTULO DE GRADO: LIC. EN EDUCACIÓN ESPECIAL, PROFESORA EN PSICOPEDAGOGÍA  HAN PERMITIDO DESEMPEÑARME EN LA FORMACIÓN DOCENTE SUPERIOR, EN LOS ESPACIOS DE FORMACIÓN PEDAGÓGICA.(DIDÁCTICA, CURRÍCULUM, PRACTICA DOCENTE, METODOLOGÍA DE LA INVESTIGACIÓN) DENTRO DEL ISFD ADEMÁS DE LAS TAREAS DOCENTES, PUDE CUMPLIR FUNCIONES EN EL DPTO DE INVESTIGACIÓN, COMO MIEMBRO Y DIRECTORA DE PROYECTOS APROBADOS POR EL INFD.', '[LICENCIADA EN EDUCACIÓN ESPECIAL -  Estado Completo]', NULL, '[MAGISTER EN CIENCIAS SOCIALES Y HUMANIDADES.CON ORIENTACIÓN EN EVALUACIÓN E INVESTIGACIÓN EDUCATIVA -  Estado Incompleto]', NULL),
(5, 5, '[CIENCIAS AGRÍCOLAS - PRODUCCIÓN ANIMAL Y LECHERÍA, CIENCIAS AGRÍCOLAS - PRODUCCIÓN ANIMAL Y LECHERÍA]', 'PARA EL ESTUDIO DEL TEMA DE INVESTIGACIÓN DE MI TESIS DOCTORAL, EN NUESTRO GRUPO DE TRABAJO HEMOS DESARROLLADO COMPETENCIAS DENTRO DE LAS ÁREAS: BIOLOGÍA REPRODUCTIVA, SEÑALIZACIÓN CELULAR, CULTIVOS CELULARES, ANÁLISIS E IDENTIFICACIÓN HORMONALES,HISTOLOGÍA Y MICROSCOPÍA.ENTRENAMIENTO EN BÚSQUEDA BIBLIOGRÁFICA, DISCUSIÓN EN SEMINARIOS DE INVESTIGACIÓN, PRESENTACIÓN EN CONGRESOS, REDACCIÓN DE INFORMES Y DE TRABAJOS DE INVESTIGACIÓN EN REVISTAS INTERNACIONALES. ACTUALMENTE SOY INTEGRANTE DE UN GRU', '[LICENCIADO EN CIENCIAS BIOLÓGICAS -  Estado Completo]', NULL, NULL, '[DOCTOR EN CIENCIAS BIOLÓGICAS -  Estado Completo]'),
(6, 6, '[CIENCIAS NATURALES Y EXACTAS - CIENCIAS DE LA TIERRA Y RELACIONADAS CON EL MEDIO AMBIENTE]', 'PROSPECCIÓN Y EXPLORACIÓN MINERA. MINERALES NO METÁLICOS Y METÁLICOS, EXPERIENCIA EN GESTIÓN MINERÍA Y  MEDIO AMBIENTAL. GIS.', '[LIC. EN CS. GEOLOGICAS -  Estado Completo]', '[ESPECIALISTA EN ING. AMBIENTAL -  Estado Incompleto, ESPECIALISTA EN DOCENCIA UNIVERSITARIA -  Estado Incompleto]', '[MAGISTER EN ING. AMBIENTAL -  Estado Incompleto]', NULL),
(7, 7, NULL, NULL, NULL, NULL, NULL, NULL),
(8, 8, '[CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS, CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS]', 'ACTUALMENTE ME ENCUENTRO REALIZANDO INVESTIGACIONES SOBRE EL MECANISMO DE ACCIÓN DE COMPUESTOS QUÍMICOS DE ORIGEN VEGETAL SOBRE EL CONTROL DE ESPECIES PLAGA DE NUECES ALMACENADAS Y EN SU DESARROLLO COMO  POSIBLES BIOPSTICIDAS.   ', '[LICENCIADO EN CIENCIAS BIOLÓGICAS -  Estado Completo]', NULL, NULL, NULL),
(9, 9, '[CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS, CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS]', 'SOY BIÓLOGO DE FORMACIÓN BÁSICA. HICE MI DOCTORADO EN CIENCIAS BIOLÓGICAS EN LA UNC EN ECOLOGÍA DE MOSQUITOS VECTORES DE FLAVIVIRUS. A SU VEZ, COMO LINEA PARALELA DE INVESTIGACIÓN ME DEDICO A LA BÚSQUEDA ALTERNATIVAS BIOLÓGICAS DE ORIGEN VEGETAL PARA EL CONTROL DE DIVERSAS PLAGAS TANTO DE INTERÉS SANITARIO COMO INTERÉS AGRÍCOLA.', '[BIÓLOGO -  Estado Completo]', NULL, NULL, '[DOCTOR EN BIOLOGÍA -  Estado Completo]'),
(10, 10, '[CIENCIAS NATURALES Y EXACTAS - CIENCIAS QUÍMICAS]', 'FARMACÉUTICA, TÍTULO OTORGADO POR LA UNC.DIPLOMATURA SUPERIOR EN FORMACIÓN DOCENTE, UNDEC - EN CURSO.MI ACTUACIÓN PROFESIONAL SE DESARROLLA EN ACTIVIDADES DE DOCENCIA EN EL NIVEL SUPERIOR EN EL ÁREA DE QUÍMICA (GENERAL, ORGÁNICA E INORGÁNICA).ACTUALMENTE PARTICIPO EN UNA LÍNEA DE INVESTIGACIÓN EN EL INSTITUTO DE INVESTIGACIÓN EDUCATIVA - UNDEC- LA ENSEÑANZA Y APRENDIZAJE DE CIENCIAS EN LA ESCUELAS SECUNDARIAS DE CHILECITO.', '[FARMACÉUTICA -  Estado Completo]', NULL, NULL, NULL),
