INSERT INTO `cvar` (`id`, `id_docente`, `areas`, `experticia`, `grado`, `especializacion`, `maestria`, `doctorado`) VALUES
(1, 1, 'CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS', 'HISTOLOGÍA: TÉCNICAS CITOHISTOLÓGICAS, HISTOQUÍMICA E INMUNOHISTOQUÍMICA. BIOPESTICIDAS DE ORIGEN VEGETAL.MECANISMOS DE ACCIÓN, VÍAS DE SEÑALIZACIÓN INTRACELULAR.REPRODUCCIÓN', 'BIOQUÍMICA -  Estado Completo', 'ESPECIALISTA EN DOCENCIA EN ENTORNOS VIRTUALES -  Estado Completo', 'MAGISTER EN DOCENCIA UNIVERSITARIA EN CIENCIAS DE LA SALUD -  Estado Completo', 'DOCTOR EN BIOQUÍMICA -  Estado Completo'),
(2, 2, 'CIENCIAS NATURALES Y EXACTAS - CIENCIAS QUÍMICAS', 'ELECTROFISIOLOGIA, MANEJO DE ANIMALES DE LABORATORIO, INMUNOHISTOQUIMICA', 'LICENCIADO EN QUÍMICA FARMACÉUTICA -  Estado Completo', 'ESPECIALISTA EN TECNOLOGÍAS MULTIMEDIA PARA DESARROLLOS EDUCATIVOS -  Estado Incompleto', NULL, 'DOCTOR EN CIENCIAS QUÍMICAS -  Estado Completo'),
(3, 3, 'CIENCIAS AGRÍCOLAS - AGRICULTURA, SILVICULTURA Y PESCA', 'PLAGAS Y ENFERMEDADES DEL NOGAL.', 'INGENIERO AGRÓNOMO -  Estado Completo', NULL, NULL, NULL),
(4, 4, 'CIENCIAS SOCIALES - CIENCIAS DE LA EDUCACIÓN', 'MI EXPERTICIA PROFESIONAL SURGE DE MI FORMACIÓN ACADÉMICA. MI TÍTULO DE GRADO: LIC. EN EDUCACIÓN ESPECIAL, PROFESORA EN PSICOPEDAGOGÍA  HAN PERMITIDO DESEMPEÑARME EN LA FORMACIÓN DOCENTE SUPERIOR, EN LOS ESPACIOS DE FORMACIÓN PEDAGÓGICA.(DIDÁCTICA, CURRÍCULUM, PRACTICA DOCENTE, METODOLOGÍA DE LA INVESTIGACIÓN) DENTRO DEL ISFD ADEMÁS DE LAS TAREAS DOCENTES, PUDE CUMPLIR FUNCIONES EN EL DPTO DE INVESTIGACIÓN, COMO MIEMBRO Y DIRECTORA DE PROYECTOS APROBADOS POR EL INFD.', 'LICENCIADA EN EDUCACIÓN ESPECIAL -  Estado Completo', NULL, 'MAGISTER EN CIENCIAS SOCIALES Y HUMANIDADES.CON ORIENTACIÓN EN EVALUACIÓN E INVESTIGACIÓN EDUCATIVA -  Estado Incompleto', NULL),
(5, 5, 'CIENCIAS AGRÍCOLAS - PRODUCCIÓN ANIMAL Y LECHERÍA, CIENCIAS AGRÍCOLAS - PRODUCCIÓN ANIMAL Y LECHERÍA', 'PARA EL ESTUDIO DEL TEMA DE INVESTIGACIÓN DE MI TESIS DOCTORAL, EN NUESTRO GRUPO DE TRABAJO HEMOS DESARROLLADO COMPETENCIAS DENTRO DE LAS ÁREAS: BIOLOGÍA REPRODUCTIVA, SEÑALIZACIÓN CELULAR, CULTIVOS CELULARES, ANÁLISIS E IDENTIFICACIÓN HORMONALES,HISTOLOGÍA Y MICROSCOPÍA.ENTRENAMIENTO EN BÚSQUEDA BIBLIOGRÁFICA, DISCUSIÓN EN SEMINARIOS DE INVESTIGACIÓN, PRESENTACIÓN EN CONGRESOS, REDACCIÓN DE INFORMES Y DE TRABAJOS DE INVESTIGACIÓN EN REVISTAS INTERNACIONALES. ACTUALMENTE SOY INTEGRANTE DE UN GRUPO DE INVESTIGACIÓN CUYO OBJETIVO ES ESTUDIAR EL EFECTO DEL ORUJO DE UVA EN EL CRECIMIENTO DE GANADO CAPRINO PARA SER REUTILIZADO YA QUE ES PRODUCTO DE DESECHO DE LA INDUSTRIA VITIVINICOLA. PARA ELLO NOS ESTAMOS ENTRENANDO EN EL MANEJO DE CAPRINOS EN CAMPO, BÚSQUEDA BIBLIOGRÁFICA, ANÁLISIS HORMONALES Y NUTRICIONALES Y PRESENTACIONES A CONGRESOS.', 'LICENCIADO EN CIENCIAS BIOLÓGICAS -  Estado Completo', NULL, NULL, 'DOCTOR EN CIENCIAS BIOLÓGICAS -  Estado Completo'),
(6, 6, NULL, NULL, NULL, NULL, NULL, NULL),
(7, 7, NULL, NULL, NULL, NULL, NULL, NULL),
(8, 8, 'CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS, CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS', 'ACTUALMENTE ME ENCUENTRO REALIZANDO INVESTIGACIONES SOBRE EL MECANISMO DE ACCIÓN DE COMPUESTOS QUÍMICOS DE ORIGEN VEGETAL SOBRE EL CONTROL DE ESPECIES PLAGA DE NUECES ALMACENADAS Y EN SU DESARROLLO COMO  POSIBLES BIOPSTICIDAS.   ', 'LICENCIADO EN CIENCIAS BIOLÓGICAS -  Estado Completo', NULL, NULL, NULL),
(9, 9, 'CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS, CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS', 'SOY BIÓLOGO DE FORMACIÓN BÁSICA. HICE MI DOCTORADO EN CIENCIAS BIOLÓGICAS EN LA UNC EN ECOLOGÍA DE MOSQUITOS VECTORES DE FLAVIVIRUS. A SU VEZ, COMO LINEA PARALELA DE INVESTIGACIÓN ME DEDICO A LA BÚSQUEDA ALTERNATIVAS BIOLÓGICAS DE ORIGEN VEGETAL PARA EL CONTROL DE DIVERSAS PLAGAS TANTO DE INTERÉS SANITARIO COMO INTERÉS AGRÍCOLA.', 'BIÓLOGO -  Estado Completo', NULL, NULL, 'DOCTOR EN BIOLOGÍA -  Estado Completo'),
(10, 10, 'CIENCIAS NATURALES Y EXACTAS - CIENCIAS QUÍMICAS', 'FARMACÉUTICA, TÍTULO OTORGADO POR LA UNC.DIPLOMATURA SUPERIOR EN FORMACIÓN DOCENTE, UNDEC - EN CURSO.MI ACTUACIÓN PROFESIONAL SE DESARROLLA EN ACTIVIDADES DE DOCENCIA EN EL NIVEL SUPERIOR EN EL ÁREA DE QUÍMICA (GENERAL, ORGÁNICA E INORGÁNICA).ACTUALMENTE PARTICIPO EN UNA LÍNEA DE INVESTIGACIÓN EN EL INSTITUTO DE INVESTIGACIÓN EDUCATIVA - UNDEC- LA ENSEÑANZA Y APRENDIZAJE DE CIENCIAS EN LA ESCUELAS SECUNDARIAS DE CHILECITO.', 'FARMACÉUTICA -  Estado Completo', NULL, NULL, NULL),
(11, 11, 'CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS', 'TAREAS REALIZADAS EJECUCIÓN Y/O SEGUIMIENTO DE ENSAYOS A CAMPO, ELABORACIÓN DE INFORMES Y PUBLICACIONES, MUESTREO DE SUELO Y MATERIAL VEGETAL. ANÁLISIS FÍSICO-QUÍMICOS Y BIOLÓGICOS DE CALIDAD DE SUELO Y PLANTA. MICROSCOPIA ÓPTICA Y ESTEREOSCÓPICA AL CUANTIFICAR EL PORCENTAJE DE INFECCIÓN DE RAÍCES CON MICORRIZAS Y CUANTIFICACIÓN DE HIFAS, ARBÚSCULOS Y VESÍCULAS. NODULACIÓN: PRUEBA DE INFECTIVIDAD EN PLÁNTULAS. FÓSFORO, NITRATOS, CARBONO DE LA BIOMASA MICROBIANA, RESPIRACIÓN Y BIOMASA MICROBIANA, COEFICIENTE METABÓLICO, FOSFATASA ÁCIDA, HIDRÓLISIS DE FLUORESCEÍNA DIACETATO, PROTEÍNAS REACTIVAS A BRADFORT.SITUACIÓN ACTUAL BECARIA CONICET. LUGAR DE TRABAJO: LABORATORIO DE ALTA COMPLEJIDAD - UNIVERSIDAD NACIONAL DE CHILECITO. TEMA DE INVESTIGACIÓN: IDENTIFICACIÓN Y CARACTERIZACIÓN DE LOS HONGOS MICORRÍCICOS ARBUSCULARES AUTÓCTONOS EN SIMBIOSIS CON OLIVO: UNA APROXIMACIÓN METABOLÓMICA HACIA LA COMPRENSIÓN DE LOS MECANISMOS FISIOLÓGICOS/BIOQUÍMICOS RELACIONADOS CON LA MITIGACIÓN DE LA SEQUÍA.', 'INGENIERA AGRÓNOMA -  Estado Completo', NULL, NULL, 'DOCTOR EN CIENCIAS AGROPECUARIAS -  Estado Incompleto'),
(12, 12, 'CIENCIAS NATURALES Y EXACTAS - CIENCIAS BIOLÓGICAS', 'REALIZO OBSERVACIONES EN MICROSCOPIO SOBRE MORFOLOGÍA VEGETAL, Y TRABAJO CON ANÁLISIS QUÍMICOS DE ALIMENTOS. OBSERVACIÓN DE AVES EN AMBIENTES AGRÍCOLAS.', 'LICENCIADA EN CIENCIAS BIOLÓGICAS. ORIENTACIÓN EN ZOOLOGÍA -  Estado Completo', NULL, 'MAGISTER EN CIENCIA Y TECNOLOGÍA DE LOS ALIMENTOS -  Estado Incompleto', NULL),
(13, 13, 'CIENCIAS SOCIALES - CIENCIAS DE LA EDUCACIÓN', 'SOY LICENCIADA Y PROFESORA EN PSICOPEDAGOGÍA. MI TAREA PROFESIONAL SE DESENVUELVE EN EL CAMPO DE LA FORMACIÓN INICIAL Y CONTINUA DE DOCENTES PARA EL NIVEL SUPERIOR UNIVERSITARIO Y NO UNIVERSITARIO. TENGO FORMACIÓN EN INVESTIGACIÓN EDUCATIVA, LO CUAL ME PERMITE DESEMPEÑARME EN EL CAMPO DE SU ENSEÑANZA COMO ASÍ TAMBIÉN EN LA PRÁCTICA DE LA MISMA. CABE DESTACAR QUE ME ENCUENTRO FINALIZANDO UNA ESPECIALIZACIÓN EN INVESTIGACIÓN Y EVALUACIÓN EDUCATIVA Y EN PROCESO DE REALIZACIÓN DE TESIS DE MAESTRÍA DEL MISMO NOMBRE. MIS TRABAJOS DE INVESTIGACIÓN HAN FOCALIZADO FUNDAMENTALMENTE EN EL CAMPO DE LA FORMACIÓN DE DOCENTES, LAS TRAYECTORIAS EDUCATIVAS, LAS REPRESENTACIONES SOCIALES SOBRE FORMACIÓN PEDAGÓGICA E INCLUSIÓN EDUCATIVA.', 'PROFESORA EN PSICOPEDAGOGÍA -  Estado Completo, LICENCIADA EN PSICOPEDAGOGÍA -  Estado Completo', 'ESPECIALISTA EN CIENCIAS SOCIALES. MENCIÓN EVALUACIÓN E INVESTIGACIÓN EDUCATIVA -  Estado Completo', 'MAGISTER EN CIENCIAS SOCIALES Y HUMANIDADES CON ORIENTACIÓN EN EVALUACIÓN E INVESTIGACIÓN EDUCATIVA -  Estado Completo', NULL),
